SELECT approvalState,id,isSign,commerceFirstId, YEAR, commerceCode, commerceName, masterId,businessDivision,businessDivisionId,masterCode,approvalId,isEffect, (CASE TYPE WHEN '0' THEN '经销商' WHEN '1' THEN '两票制经销商' WHEN '2' THEN '终止认可' WHEN '3' THEN '转分销商' ELSE '未选择' END) AS TYPE, acceptState,memo FROM v_agreementlistmaster
${whereSql}
ORDER BY ${orderBySql}
ORDER BY id DESC

