mapper.select:
SELECT a.*,b.id,b.agreementSummaryId, IFNULL(b.isSign,'0') AS isSign,b.effectiveDate,b.isBidding,b.isImplement,b.biddingPrice, IFNULL(b.supplyPrice,0.0) AS supplyPrice,IFNULL(b.allotGross,0) AS allotGross, IFNULL(b.acceptGross,0) AS acceptGross,IFNULL(b.purePinGross,0) AS purePinGross, b.wireTransferPrice,b.acceptPrice,b.purePinPrice,b.memo,0 AS delivery FROM ( (SELECT b.OTC AS gaugeType,b.productCode AS commerceCode, b.commonName AS commerceGauge,b.id AS productId,a.id AS productFirstId FROM scm_zsj_productfirst a LEFT JOIN scm_zsj_product b ON a.productId = b.id WHERE a.approvalState = 2 ) a LEFT JOIN (SELECT * FROM scm_xs_agreementproductdetail WHERE agreementSummaryId = IFNULL( (SELECT MAX(a.id) FROM scm_xs_agreementsummary a WHERE a.approvalstate = 2 AND a.businessDivisionId = #{arg0}
AND a.provinceId=#{arg1}
AND a.ownerId=#{arg2}),-1) ) b ON b.commerceCode = a.commerceCode )

SELECT a.productName AS commerceGauge,a.id AS productId,a.productCode as commerceCode,b.id AS productFirstId, IFNULL(c.isSign,'0') AS lastIsSign,d.isSign, IFNULL(d.effectiveDate,DATE_SUB(CURDATE(),INTERVAL DAYOFYEAR(NOW())-1 DAY)) AS effectiveDate, 0 AS delivery, a.OTC,1 AS isBidding,1 AS isImplement, 30 AS biddingPrice,d.agreementSummaryId,
IFNULL(c.supplyPrice,30) AS lastSupplyPrice,IFNULL(c.supplyPrice,30) AS supplyPrice, IFNULL(c.allotGross,0) AS lastAllotGross,D.allotGross, IFNULL(c.acceptGross,0) AS lastAcceptGross,d.acceptGross, IFNULL(c.purePinGross,0) AS lastPurePinGross,d.purePinGross, d.wireTransferPrice,d.acceptPrice,d.purePinPrice, d.memo FROM scm_zsj_product a INNER JOIN scm_zsj_productfirst b ON a.id = b.productId AND b.ownerId = #{arg2} AND b.approvalState = 2 LEFT JOIN (SELECT * FROM scm_xs_agreementproductdetail WHERE agreementSummaryId = IFNULL( (SELECT MAX(id) AS masterId FROM scm_xs_agreementsummary WHERE approvalState = 2 AND ownerid = #{arg2} AND businessDivisionId = #{arg0}
AND provinceId = #{arg1}
GROUP BY ownerId) ,-1) ) c ON a.productCode = c.commerceCode LEFT JOIN scm_xs_agreementproductdetail d ON a.productCode = d.commerceCode AND d.agreementSummaryId = #{arg3}

SELECT * FROM SCM_XS_AgreementProductDetail


		${whereSql}



	ORDER BY ${orderBySql}


	ORDER BY id DESC
