mapper.select:
SELECT * FROM SCM_ZSJ_ProductFirst WHERE
productId=#{productId} and
ownerId=#{ownerId} and supplierId=#{supplierId}

SELECT * FROM SCM_ZSJ_ProductFirst 
WHERE 
ownerId=#{ownerId} and supplierId=#{supplierId} and productId=#{productId}

SELECT * FROM ( SELECT a.id,a.productId,a.ownerId,a.approvalId,a.approvalState,a.supplierId,a.supplier,a.approvalCode,a.approvalValidity, a.businessScope,a.gmp,a.gmpValidity,a.startBatch,a.qualityStandard,a.length,a.wide,a.high,a.volume,a.roughWeight,a.cartonBarcode, a.smallBarcode,b.productName,b.ProductLineId,b.ProductLineName,b.productCode,b.commonName,b.productState,b.AgentTypeId,b.AgentType, b.unit,b.OTC,b.traceCode,b.MedicineTypeId,b.MedicineType,b.medicineClassifyId,b.medicineClassify,b.KeepingCondition,b.suggestWarehouse, b.supplierName,b.manufacturer,b.InventoryCode,b.operatorName, b.operatorDate FROM SCM_ZSJ_ProductFirst a LEFT JOIN scm_zsj_product b ON b.id=a.productId ) c
${whereSql}
ORDER BY ${orderBySql}
ORDER BY id DESC
